package lab3package;

import java.io.IOException;
import java.io.PrintWriter;
import java.util.ArrayList;
import java.util.List;

import javax.servlet.ServletException;
import javax.servlet.annotation.WebServlet;
import javax.servlet.http.HttpServlet;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;

@WebServlet("/FoodMenuServlet")
public class FoodMenuServlet extends HttpServlet {
	private static final long serialVersionUID = 1L;
   
    public FoodMenuServlet() {
        super();
        // TODO Auto-generated constructor stub
    }
    public void init() {
    	List<FoodEntries> entries = new ArrayList<>();
		entries.add(new FoodEntries(entries.size(), "The Vanessa", "Cold Drink with mint and cherries","https://premiumredtea.files.wordpress.com/2016/02/header.jpg?w=300",5));
		entries.add(new FoodEntries(entries.size(), "The Cold Berry", "Green tea and kiwi","data:image/jpeg;base64,",10));
		getServletContext().setAttribute("entries", entries);
    }

	protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
		// TODO Auto-generated method stub
		//response.getWriter().append("Served at: ").append(request.getContextPath());
		PrintWriter out = response.getWriter();
		List<FoodEntries> entries = (List<FoodEntries>) getServletContext().getAttribute("entries");
		// tell browser this is html document
		response.setContentType("text/html");

		out.println("<head>");
		out.println("<style>body { " +
		"}</style>");
		out.println("</head>");

		out.println("<h1> Welcome to Food Menu! </h1>");
		out.println("<table border='2'>");
		for (FoodEntries entry: entries) {
			out.println(
				"<tr>" + 
					"<td>" + entry.getFoodName() + "</td>" + 
					"<td>" + entry.getDescription() + "</td>" +
					"<td>" + "<img src= '" + entry.getFoodImageURL() + "' height='50' width='50'></td>" +
					"<td>" + entry.getFoodPrice() + "</td>" +
					"<td><a href='FoodMenuServlet/edit?id=" + entry.getFoodID() + "'>Edit</a> " + 
					"<a href='FoodMenuServlet/delete?id=" + entry.getFoodID() + "'>Delete</a></td>" +
					
				"</tr>"
			);
		}
		out.println("</table>");
		out.println("<a href='FoodMenuServlet/create'>Add a Food item</a>");
		out.println("<a href='http://localhost:8080/CS3220-Summer-Lab3/foodlist'>Check your Menu</a>");
		out.println("</br>");
		out.println("</br>");
	}

	
	protected void doPost(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
		// TODO Auto-generated method stub
		//doGet(request, response);
	}

}
